// Root route
app.get('/', (req, res) => {
  res.json({
    message: "Welcome to the Quotes API!",
    endpoints: {
      "GET /quotes": "Get all quotes",
      "GET /quotes/:id": "Get a specific quote by ID",
      "POST /quotes": "Add a new quote (requires 'text' and optionally 'author' in body)",
      "PUT /quotes/:id": "Update a quote by ID",
      "DELETE /quotes/:id": "Delete a quote by ID",
      "GET /quotes/random": "Get a random quote"
    },
    totalQuotes: quotes.length
  });
});

// Get all quotes
app.get('/quotes', (req, res) => {
  res.json({
    success: true,
    count: quotes.length,
    data: quotes
  });
});

// Get a random quote
app.get('/quotes/random', (req, res) => {
  const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
  res.json({
    success: true,
    data: randomQuote
  });
});

// Get a single quote by id
app.get('/quotes/:id', (req, res) => {
  const quote = quotes.find(q => q.id === parseInt(req.params.id));
  if (quote) {
    res.json({
      success: true,
      data: quote
    });
  } else {
    res.status(404).json({ 
      success: false,
      error: 'Quote not found' 
    });
  }
});

// Add a new quote
app.post('/quotes', (req, res) => {
  const { text, author } = req.body;
  if (!text) {
    return res.status(400).json({ 
      success: false,
      error: 'Text is required' 
    });
  }
  const newQuote = { 
    id: quotes.length > 0 ? Math.max(...quotes.map(q => q.id)) + 1 : 1, 
    text,
    author: author || 'Unknown'
  };
  quotes.push(newQuote);
  res.status(201).json({
    success: true,
    message: 'Quote added successfully',
    data: newQuote
  });
});

// Update a quote
app.put('/quotes/:id', (req, res) => {
  const { text, author } = req.body;
  const quote = quotes.find(q => q.id === parseInt(req.params.id));
  if (!quote) {
    return res.status(404).json({ 
      success: false,
      error: 'Quote not found' 
    });
  }
  if (!text) {
    return res.status(400).json({ 
      success: false,
      error: 'Text is required' 
    });
  }
  quote.text = text;
  if (author) quote.author = author;
  res.json({
    success: true,
    message: 'Quote updated successfully',
    data: quote
  });
});

// Delete a quote
app.delete('/quotes/:id', (req, res) => {
  const id = parseInt(req.params.id);
  const index = quotes.findIndex(q => q.id === id);
  if (index === -1) {
    return res.status(404).json({ 
      success: false,
      error: 'Quote not found' 
    });
  }
  const deletedQuote = quotes.splice(index, 1)[0];
  res.json({
    success: true,
    message: 'Quote deleted successfully',
    data: deletedQuote
  });
});

app.listen(port, () => {
  console.log(`Quotes API listening at http://localhost:${port}`);
});
